import { StrictMode, useEffect, useState } from "react";
import ReactDOM from "react-dom";

import App from "./App";

const params = new URLSearchParams(window.location.search);
const text = params.get("text") || "play";

function Wrapper() {
  const [buttonText, setButtonText] = useState("play");

  useEffect(() => {
    window.addEventListener("message", handleMessage);
    return () => window.removeEventListener("message", handleMessage);
  }, []);

  const handleMessage = (event) => {
    // Optional: Add origin check for security
    const { type, payload } = event.data;

    if (type === "SET_BUTTON_TEXT") {
      setButtonText(payload.text || "play");
    }

    if (type === "CLICK_CALLBACK") {
      // You can trigger some other function if needed
      console.log("Custom iframe command received:", payload);
    }
  };

  const handleClick = () => {
    // Send message back to parent window
    window.parent.postMessage({ type: "BUTTON_CLICKED" }, "*");
  };

  return <App buttonText={buttonText} onButtonClick={handleClick} />;
}

const rootElement = document.getElementById("root");
ReactDOM.render(
  <StrictMode>
    <App buttonText={text} />
    </StrictMode>,
  rootElement
);
